insert into MOVIE (id,name, experiences, listing_type, lang, image_name, stills, synopsis) values (2,'Theri', 'RDX, Dolby Atmos, SUB', 'NOW_SHOWING',6,'theri','theri_1,theri_2','Concealing his identity, DCP Vijaya Kumar goes into self-inflicted exile to bring up his daughter. However, when her life is threatened by gangsters, he must settle an old score with a politician.');
insert into MOVIE (id,name, experiences, listing_type, lang, image_name, stills, synopsis) values (3,'Banjo', 'RDX 3D', 'NOW_SHOWING',6,'banjo','banjo_1,banjo_2','Taraat, a banjoist hailing from a poor background, and his associates get the offer of a lifetime when they are approached by Christina, a musician from the US.');
