insert into MOVIE (id,name, experiences, listing_type, lang, image_name, stills, synopsis) values (5,'Namadhu', 'RDX, SUB', 'NOW_SHOWING',2,'namadhu','namadhu_1,namadhu_2','The lives of four middle-class individuals, Sairam, Gayathri, Abhiram and Mahitha, collide as they pursue their individual ambitions amidst various challenges.');
