insert into MOVIE (id,name, experiences, listing_type, lang, image_name, stills, synopsis) values (7,'Pink', 'RDX, SUB', 'NOW_SHOWING',6,'pink','pink_1,pink_2','After being molested, Minal, along with her friends, tries to file an FIR against a politicians nephew. When the subsequent case gets rigged, Deepak, a retired lawyer, helps them to fight the case.');
insert into MOVIE (id,name, experiences, listing_type, lang, image_name, stills, synopsis) values (8,'Remo', 'Dolby Atmos, RDX, SUB', 'NOW_SHOWING',2,'remo','remo_1,remo_2','An aspiring actor falls in love with Kavya, a doctor. Upon knowing that she is already engaged, he disguises himself as a female nurse with the aim of breaking her engagement.');
