insert into MOVIE (name, experiences, listing_type, lang, image_name, stills, synopsis) values ('Kabali', 'RDX, Dolby Atmos, SUB', 'UPCOMING',2,'kabali','kabali_1,kabali_2','A man attempts to protect his family from enemies, gained in his time as a gangster. After he is released from prison on false charges, he resumes his post as gang leader and continues a long-held rivalry.');
insert into MOVIE (name, experiences, listing_type, lang, image_name, stills, synopsis) values ('Theri', 'RDX, Dolby Atmos, SUB', 'UPCOMING',6,'theri','theri_1,theri_2','Concealing his identity, DCP Vijaya Kumar goes into self-inflicted exile to bring up his daughter. However, when her life is threatened by gangsters, he must settle an old score with a politician.');
insert into MOVIE (name, experiences, listing_type, lang, image_name, stills, synopsis) values ('Banjo', 'RDX 3D', 'UPCOMING',6,'banjo','banjo_1,banjo_2','Taraat, a banjoist hailing from a poor background, and his associates get the offer of a lifetime when they are approached by Christina, a musician from the US.');
insert into MOVIE (name, experiences, listing_type, lang, image_name, stills, synopsis) values ('Suicide Squad', 'Dolby Atmos, RDX 3D, SUB', 'UPCOMING',1,'suicide-squad','suicide-squad_1,suicide-squad_2','Figuring they are all expendable, a U.S. intelligence officer decides to assemble a team of dangerous, incarcerated supervillains for a top-secret mission. Now armed with government weapons, Deadshot (Will Smith), Harley Quinn (Margot Robbie), Captain Boomerang, Killer Croc and other despicable inmates must learn to work together. Dubbed Task Force X, the criminals unite to battle a mysterious and powerful entity, while the diabolical Joker (Jared Leto) launches an evil agenda of his own.');
insert into MOVIE (name, experiences, listing_type, lang, image_name, stills, synopsis) values ('Namadhu', 'RDX, SUB', 'UPCOMING',2,'namadhu','namadhu_1,namadhu_2','The lives of four middle-class individuals, Sairam, Gayathri, Abhiram and Mahitha, collide as they pursue their individual ambitions amidst various challenges.');